mapper.select:
SELECT

FROM user_ceshi a

WHERE a.IsDeleted = 1 and a.id = #{id}

SELECT

FROM user_ceshi a


    a.IsDeleted = 1

        AND a.name = #{name}




        ORDER BY ${orderBy}


        ORDER BY a.CreateTime DESC

mapper.update:
UPDATE user_ceshi SET
    name = #{name}
    UpdateUserId = '${@hans.common.util.CurrentUser@oauthId()}',
    UpdateUserName = '${@hans.common.util.CurrentUser@oauthName()}',
    UpdateTime = NOW()
WHERE id = #{id}

UPDATE user_ceshi SET

                    name = #{name}

    UpdateUserId = '${@hans.common.util.CurrentUser@oauthId()}',
    UpdateUserName = '${@hans.common.util.CurrentUser@oauthName()}',
    UpdateTime = NOW()
WHERE id = #{id}

